import React from 'react'
import { Divider, IconButton, InputBase, useTheme } from '@mui/material'
import { Box } from '@mui/system';
import WidgetWrapper from '../components/WidgetWrapper';
import { useSelector } from 'react-redux';
import UserImage from '../components/UserImage';





function MyPostWidget() {
    const { palette } = useTheme();
    const {_id,picturePath} = useSelector((state) => state.user);
  return (
   <Box>
    <WidgetWrapper>
        <Box display={"flex"} gap={"1rem"} pb="1.1rem">
            <UserImage image={picturePath} />
            <Box display={"flex"} flexGrow={"1"} alignItems="center" padding="0.1rem 1.5rem" backgroundColor={palette.neutral.light} borderRadius="50px">
                <InputBase placeholder="What's on your mind..." />
            </Box>
        </Box>
        <Divider />
    </WidgetWrapper>
   </Box>
  )
}

export default MyPostWidget
