import React from 'react'
import { Divider, IconButton, InputBase, useTheme } from '@mui/material'
import { Box } from '@mui/system';
import WidgetWrapper from '../components/WidgetWrapper';
import { useSelector } from 'react-redux';
import UserImage from '../components/UserImage';
import FlexBetween from '../components/FlexBtween';





function MyPostWidget() {
    const { palette } = useTheme();
    const {_id,picturePath} = useSelector((state) => state.user);
  return (
   <Box>
    <WidgetWrapper>
        <FlexBetween gap={"1.5rem"}>
            <UserImage image={picturePath} />
            <InputBase sx={{width:'100%',backgroundColor: palette.neutral.light,borderRadius: "2rem", padding: "1rem 2rem"}} placeholder="What's on your mind..." />
        </FlexBetween>
        <Divider />
    </WidgetWrapper>
   </Box>
  )
}

export default MyPostWidget
